import './App.css';
import React, { useState } from 'react';
import DatosCliente from './components/DatosCliente';
import DatosVehiculo from './components/DatosVehiculo';
import SeleccionServicios from './components/SeleccionServicios';

/*
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}
*/
const App = () => {
  const [step, setStep] = useState(1);
  const [cliente, setCliente] = useState(null);
  const [vehiculo, setVehiculo] = useState(null);
  const [servicios, setServicios] = useState(null);
  const [theme, setTheme] = useState('light');

  const handleNextStep = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const handleClienteSubmit = (datosCliente) => {
    setCliente(datosCliente);
    handleNextStep();
  };

  const handleVehiculoSubmit = (datosVehiculo) => {
    setVehiculo(datosVehiculo);
    handleNextStep();
  };

  const handleServiciosSubmit = (serviciosSeleccionados) => {
    setServicios(serviciosSeleccionados);
    handleNextStep();
  };

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  return (
    <div className={'App ${theme}'}>
	   <button onClick={toggleTheme}>Cambiar Tema</button>
      {step === 1 && <DatosCliente onNext={handleClienteSubmit} />}
      {step === 2 && <DatosVehiculo onNext={handleVehiculoSubmit} />}
      {step === 3 && <SeleccionServicios onNext={handleServiciosSubmit} />}
      {step === 4 && (
        <div>
          <h2>Orden de Trabajo</h2>
          <p>Cliente: {cliente.nombre}</p>
          <p>Email: {cliente.email}</p>
          <p>Contacto: {cliente.contacto}</p>
          <p>Identificación Fiscal: {cliente.identificacion}</p>
          <p>Tipo de Identificación: {cliente.tipoIdentificacion}</p>

          <p>Marca del Vehículo: {vehiculo.marca}</p>
          <p>Modelo del Vehículo: {vehiculo.modelo}</p>
          <p>Placa del Vehículo: {vehiculo.placa}</p>
          <p>Nivel de Gasolina: {vehiculo.nivelGasolina}</p>
          <p>Estado Exterior: {vehiculo.estadoExterior}</p>

          <p>Servicios Seleccionados:</p>
          <ul>
            {Object.entries(servicios).map(([servicio, seleccionado]) => (
              <li key={servicio}>{servicio}: {seleccionado ? 'Sí' : 'No'}</li>
            ))}
          </ul>

          <p>Fecha y Hora Estimada de Entrega: [Agregar aquí]</p>
        </div>
      )}
    </div>
  );
};

export default App;
